mapper.update:
update company set updatetime=now()

	,companyname=#{companyname}


	,legalperson=#{legalperson}


	,organizationcode=#{organizationcode}


	,businesslicense=#{businesslicense}


	,fax=#{fax}


	,address=#{address}


	,mail=#{mail}


	,website=#{website}

where id=#{id}
