mapper.select:
select
id,userid,companyid,type,title,content from
news where id = #{id}

select a.id,a.type,a.title,b.companyname,a.createtime from news a left join company b on a.companyid=b.id where isdel=0
and type = #{type}
order by createtime desc

mapper.update:
update news set updatetime=now()

	,title=#{title}


	,content=#{content}

where id = #{id}
